SELECT u.PartnerUserId, MAX(u.date)FROM
(

    SELECT u.PartnerUserId, MAX(o.CalculationDate_DT) date
    FROM casino.orders o
        INNER JOIN VIEW_PlatformPartnerUsers_TotogamingAm u on o.UserID = u.UserID
    WHERE u.PartnerUserId IN (100386546,101113930,100600845,100707299,100958363,101175805,101161662,101143927,100797147,100830683,101083993,100386107,101001818,100387702,100653446,100917574,100727913,100682862,101021428,101015935,100838971,100705834,100981526,100947158,100586669,100400166,100956216,100746093,101172810,100899499,100746417,101014339,100407458,100676732,100942474,100743492,100499341,100394019,100388759,100757191)
      AND o.CalculationDate_DT >= '2022-01-01'
      AND o.OperationTypeID IN (3,299)
      AND o.OrderStateID    IN (2,3,5,6,8,9,10)
    group by u.PartnerUserId
    UNION
    SELECT DISTINCT(u.PartnerUserId), MAX(o.CalculationDate_DT) date
    FROM VIEW_sport_OrdersBetsStakes_TotogamingAm o
        INNER JOIN VIEW_sport_PartnerUser_TotogamingAm u on o.UserID = u.UserID
    WHERE u.PartnerUserId IN (100386546,101113930,100600845,100707299,100958363,101175805,101161662,101143927,100797147,100830683,101083993,100386107,101001818,100387702,100653446,100917574,100727913,100682862,101021428,101015935,100838971,100705834,100981526,100947158,100586669,100400166,100956216,100746093,101172810,100899499,100746417,101014339,100407458,100676732,100942474,100743492,100499341,100394019,100388759,100757191)
      AND o.CalculationDate_DT >= '2022-01-01'
      AND o.DeviceTypeID <> 1
      AND o.OrderStateID    IN (2,3,5,6,8,9,10)
    group by u.PartnerUserId) u
group by u.PartnerUserId
